import { BadlyFormattedAddressError, IdentityProviderAddressInvalidError } from './errors.js';

type ValidateAddressError = BadlyFormattedAddressError | IdentityProviderAddressInvalidError;
/**
 * Performs offline checks to see if an address is invalid. Even if there is no error addresses need to be resolved to be sure they are valid.
 *
 * @param address fully qualified address to check
 * @param mailchainDomain
 * @returns an error if it's invalid or undefined if validation passes.
 */
declare function checkAddressForErrors(address: string, mailchainDomain?: string): ValidateAddressError | undefined;

export { ValidateAddressError, checkAddressForErrors };
