// src/protocols/consts.ts
var ALGORAND = "algorand";
var ETHEREUM = "ethereum";
var SUBSTRATE = "substrate";
var NEAR = "near";
var TEZOS = "tezos";
var SOLANA = "solana";
var MAILCHAIN = "mailchain";
var ALL_PROTOCOLS = [ALGORAND, ETHEREUM, SUBSTRATE, NEAR, MAILCHAIN, TEZOS, SOLANA];
var ENABLED_PROTOCOLS = [ETHEREUM, NEAR, TEZOS, SOLANA];
function isBlockchainProtocolEnabled(protocol) {
  return ENABLED_PROTOCOLS.includes(protocol);
}

export {
  ALGORAND,
  ETHEREUM,
  SUBSTRATE,
  NEAR,
  TEZOS,
  SOLANA,
  MAILCHAIN,
  ALL_PROTOCOLS,
  isBlockchainProtocolEnabled
};
