import {
  solanaAddressFromPublicKey
} from "./chunk-X7CUXOZW.mjs";
import {
  tezosAddressFromPublicKey
} from "./chunk-GGYP7UAT.mjs";
import {
  addressFromPublicKey
} from "./chunk-DGSHHA5W.mjs";
import {
  ETHEREUM,
  SOLANA,
  TEZOS
} from "./chunk-53HGU7GY.mjs";

// src/addressFromPublicKey.ts
async function addressFromPublicKey2(publicKey, protocol) {
  switch (protocol) {
    case ETHEREUM:
      return addressFromPublicKey(publicKey);
    case TEZOS:
      return tezosAddressFromPublicKey(publicKey);
    case SOLANA:
      return solanaAddressFromPublicKey(publicKey);
    default:
      throw new Error(`address from PublicKey for {${protocol}} not unsupported`);
  }
}

export {
  addressFromPublicKey2 as addressFromPublicKey
};
