// src/protocols/solana/address.ts
import { KindED25519 } from "@mailchain/crypto";
import { decodeBase58 } from "@mailchain/encoding";
function validateSolanaAddress(addressID) {
  try {
    const address = decodeBase58(addressID);
    return address.length === 32;
  } catch (e) {
    return false;
  }
}
function solanaAddressFromPublicKey(publicKey) {
  if (publicKey.curve !== KindED25519)
    throw new Error("Only ED25519 is supported for Solana");
  if (publicKey.bytes.length !== 32)
    throw new Error("Invalid public key length for Solana");
  return publicKey.bytes;
}

