import {
  ALGORAND,
  ETHEREUM,
  MAILCHAIN,
  NEAR,
  SOLANA,
  SUBSTRATE,
  TEZOS
} from "./chunk-53HGU7GY.mjs";

// src/addressCasing.ts
function casingByProtocol(value, protocol) {
  switch (protocol) {
    case MAILCHAIN:
      return value.toLowerCase();
    case ETHEREUM:
      return value.toLowerCase();
    case SOLANA:
    case SUBSTRATE:
    case TEZOS:
      return value;
    case ALGORAND:
      return value.toLowerCase();
    case NEAR:
      return value.toLowerCase();
    default:
      throw new Error(`casing for protocol [${protocol}] not defined`);
  }
}

export {
  casingByProtocol
};
