// src/nameservices/matchesNameservice.ts
function matchesNameservice(address, nameserviceDescription) {
  const domainParts = address.domain.split(".");
  if (domainParts[0] !== nameserviceDescription.name)
    return void 0;
  const usernameParts = address.username.split(".");
  return nameserviceDescription.domains.find((domain) => usernameParts[usernameParts.length - 1] === domain);
}

export {
  matchesNameservice
};
