import { EncodingType } from '@mailchain/encoding';
import { ProtocolType } from './protocols/consts.js';

/**
 * Convert address from Uint8Array, selects the relevant encoding method and encodes it as string.
 */
declare function encodeAddressByProtocol(address: Uint8Array, protocol: ProtocolType): {
    encoded: string;
    encoding: EncodingType;
};
/**
 * Convert address from string, selects the relevant encoding method and decodes it as Uint8Array.
 */
declare function decodeAddressByProtocol(address: string, protocol: ProtocolType): {
    decoded: Uint8Array;
    encoding: string;
};
/**
 * EncodingByProtocol returns the relevant encoding method the protocol commonly uses.
 */
declare function encodingByProtocol(protocol: ProtocolType): EncodingType;

export { decodeAddressByProtocol, encodeAddressByProtocol, encodingByProtocol };
