import { PublicKey } from '@mailchain/crypto';

/**
 * Derive the ethereum address corresponding to the {@link PublicKey}.
 *
 * @param publicKey must be with with {@link KindSECP256K1} curve
 * @throw if the provided key is on unsupported curve
 */
declare function addressFromPublicKey(publicKey: PublicKey): Promise<Uint8Array>;
declare function validateEthereumAddress(address: string): boolean;
declare function validateEthereumTokenOwnerAddress(address: string): boolean;

export { addressFromPublicKey, validateEthereumAddress, validateEthereumTokenOwnerAddress };
