import {
  SECP256K1PublicKey
} from "./chunk-ULGLISTA.mjs";
import {
  SECP256R1PublicKey
} from "./chunk-XSRU2W5H.mjs";
import {
  ED25519PublicKey
} from "./chunk-5SHWDSQB.mjs";
import {
  KindED25519,
  KindSECP256K1,
  KindSECP256R1
} from "./chunk-6E4UZ7KY.mjs";

// src/multikey/names.ts
function kindFromPublicKey(key) {
  switch (key.constructor) {
    case SECP256K1PublicKey:
      return KindSECP256K1;
    case ED25519PublicKey:
      return KindED25519;
    case SECP256R1PublicKey:
      return KindSECP256R1;
    default:
      throw RangeError("unknown public key type");
  }
}
function publicKeyFromKind(kind, data) {
  switch (kind) {
    case KindSECP256K1:
      return new SECP256K1PublicKey(data);
    case KindED25519:
      return new ED25519PublicKey(data);
    case KindSECP256R1:
      return new SECP256R1PublicKey(data);
    default:
      throw RangeError("unknown public key type");
  }
}

export {
  kindFromPublicKey,
  publicKeyFromKind
};
