import { PublicKey } from './public.js';

interface PrivateKey extends SignerWithPublicKey {
    readonly bytes: Uint8Array;
}
interface SignerWithPublicKey extends Signer {
    readonly publicKey: PublicKey;
}
interface Signer {
    sign(message: Uint8Array): Promise<Uint8Array>;
    curve: string;
}

export { PrivateKey, Signer, SignerWithPublicKey };
