// src/mailboxRuleEngine/actions.ts
function isActionAddSystemLabel(action) {
  return action.type === "AddSystemLabel";
}
function actionAddSystemLabel(label) {
  return {
    type: "AddSystemLabel",
    value: label
  };
}
function isActionRemoveSystemLabel(action) {
  return action.type === "RemoveSystemLabel";
}
function actionRemoveSystemLabel(label) {
  return {
    type: "RemoveSystemLabel",
    value: label
  };
}

export {
  isActionAddSystemLabel,
  actionAddSystemLabel,
  isActionRemoveSystemLabel,
  actionRemoveSystemLabel
};
