// src/verifiableCredentials/did.ts
function mailchainAddressDecentralizedIdentifier(address) {
  return `did:mailchain:${encodeURIComponent(address)}`;
}
function isMailchainAddressDecentralizedIdentifier(did) {
  return did.startsWith("did:mailchain:");
}
function mailchainAddressFromDecentralizedIdentifier(did) {
  return decodeURIComponent(did.replace("did:mailchain:", ""));
}
function mailchainMessagingKeyDecentralizedIdentifier(address) {
  return `${mailchainAddressDecentralizedIdentifier(address)}/messaging-key`;
}
function mailchainBlockchainAddressDecentralizedIdentifier(protocol, protocolAddress) {
  return `did:mailchain:${protocol}:${protocolAddress}`;
}

export {
  mailchainAddressDecentralizedIdentifier,
  isMailchainAddressDecentralizedIdentifier,
  mailchainAddressFromDecentralizedIdentifier,
  mailchainMessagingKeyDecentralizedIdentifier,
  mailchainBlockchainAddressDecentralizedIdentifier
};
