type MailboxRuleCondition<V> = {
    type: string;
    value: V;
};
type MailboxRuleAction<V> = {
    type: string;
    value: V;
};
type MailboxRule<C, A> = {
    id: string;
    name: string;
    isEnabled: () => Promise<boolean>;
    condition: () => Promise<MailboxRuleCondition<C>>;
    actions: () => Promise<MailboxRuleAction<A>[]>;
    stopFurtherProcessing?: boolean;
};

export { MailboxRule, MailboxRuleAction, MailboxRuleCondition };
