import { EncryptedPayload } from './payload.js';

declare enum MessageKind {
    UNSET = 0,
    HEADER = 1,
    CHUNK = 2,
    FINAL_CHUNK = 3
}
declare function serializeMessage(encryptedMessage: Buffer, messageKind: MessageKind): Buffer;
declare function serialize(input: EncryptedPayload): Buffer;
declare function deserializeMessage(serializedMessage: Buffer): {
    kind: MessageKind;
    message: Buffer;
    remainingBuffer: Buffer;
};
declare function deserialize(serializedData: Buffer): EncryptedPayload;

export { MessageKind, deserialize, deserializeMessage, serialize, serializeMessage };
