// src/hasOnlyAscii.ts
var SP_CHAR_CODE = 32;
var HTAB_CHAR_CODE = 9;
function hasOnlyPrintableUsAscii(str, allowWSC = true) {
  for (let i = 0; i < str.length; i++) {
    const charCode = str.charCodeAt(i);
    if (charCode >= 33 && charCode <= 126 || allowWSC && (charCode === SP_CHAR_CODE || charCode === HTAB_CHAR_CODE)) {
      continue;
    }
    return false;
  }
  return true;
}

export {
  hasOnlyPrintableUsAscii
};
