// src/mailchain_message_confirmation.ts
import { encodeHex } from "@mailchain/encoding";
var mailchainDeliveryConfirmationMessage = (deliveryRequestID) => {
  return Buffer.from(`Mailchain delivery confirmation:
${encodeHex(deliveryRequestID)}`, "utf-8");
};
var signMailchainDeliveryConfirmation = (pk, deliveryRequestID) => {
  const msg = mailchainDeliveryConfirmationMessage(deliveryRequestID);
  return pk.sign(msg);
};

export {
  mailchainDeliveryConfirmationMessage,
  signMailchainDeliveryConfirmation
};
