import { SignerWithPublicKey, PublicKey, EncryptedContent, PlainContent } from '@mailchain/crypto';

interface KeyRingDecrypter extends SignerWithPublicKey {
    ecdhDecrypt(bundleEphemeralKey: PublicKey, input: EncryptedContent): Promise<PlainContent>;
}

declare const mailchainDeliveryConfirmationMessage: (deliveryRequestID: Uint8Array) => Uint8Array;
declare const signMailchainDeliveryConfirmation: (pk: KeyRingDecrypter, deliveryRequestID: Uint8Array) => Promise<Uint8Array>;

export { mailchainDeliveryConfirmationMessage, signMailchainDeliveryConfirmation };
